import { FIND_DETAILS } from '../actions/DetailsAction';

const initialState = {
  details: [],
  totalRuntime: 0,
};

const DetailsReducer = (state = initialState, action) => {
  switch (action.type) {
    case FIND_DETAILS:
      let time = 0;
      return {
        details: action.details,
        totalRuntime: action.details.tracks.items.map((item) => (time += item.track.duration_ms)),
      };
    default:
      return state;
  }
};

export default DetailsReducer;
